document.addEventListener('DOMContentLoaded', (event) => {
    let showTimeout;
    let hideTimeout;
  
    function showSVG() {
      document.getElementById('animated-svg').style.display = 'block';
      hideTimeout = setTimeout(hideSVG, 5000);  // Show for 3 seconds
    }
  
    function hideSVG() {
      document.getElementById('animated-svg').style.display = 'none';
      showTimeout = setTimeout(showSVG, 6000);  // Hide for 4 seconds
    }
  
    // Start the initial cycle immediately
    showSVG();  // Start by showing the SVG
  });
  
  